import React, { useState, useEffect } from 'react';
import { Table, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import alunoService from '../services/alunoService';

function AlunoList() {
  const [alunos, setAlunos] = useState([]);

  useEffect(() => {
    fetchAlunos();
  }, []);

  const fetchAlunos = async () => {
    const data = await alunoService.getAllAlunos();
    setAlunos(data);
  };

  const handleDelete = async (id) => {
    await alunoService.deleteAluno(id);
    fetchAlunos();
  };

  const [selectedOption, setSelectedOption] = useState("");

  const handleSelectChange = (event) => {
    const selectedValue = event.target.value;
    setSelectedOption(selectedValue);
    alert(`Você selecionou: ${selectedValue}`);
  };

  return (
    <div>
      <h2>Lista de Alunos</h2>
      <select id="aluno-select" value={selectedOption} onChange={handleSelectChange}>
        <option value="">Selecione...</option>
        <option value="1">Média maior que 7</option>
        <option value="Aluno 2">Aluno 2</option>
        <option value="Aluno 3">Aluno 3</option>
      </select>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Curso</th>
            <th>IRA</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {alunos.map((aluno) => (

            <tr key={aluno.id}>
              <td>{aluno.nome}</td>
              <td>{aluno.curso}</td>
              <td>{aluno.ira}</td>
              <td>
                <Link to={`/alunos/editar/${aluno.id}`} className="btn btn-sm btn-primary mr-2">Editar</Link>
                <Button variant="danger" size="sm" onClick={() => handleDelete(aluno.id)}>Excluir</Button>
              </td>
            </tr>

          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default AlunoList;
